import{_ as t,o as i,c as o,a3 as n}from"./chunks/framework.-fNtKYRb.js";const u=JSON.parse('{"title":"English for post Writing Guidelines","description":"English for post Writing Guidelines","frontmatter":{"title":"English for post Writing Guidelines","recommend":1,"author":"rx-ted","date":"2024-01-01T00:00:00.000Z","tag":["guide"],"description":"English for post Writing Guidelines","sticky":4},"headers":[],"relativePath":"notes/posts/English for post Writing Guidelines.md","filePath":"notes/posts/English for post Writing Guidelines.md","lastUpdated":1726816794000}'),s={name:"notes/posts/English for post Writing Guidelines.md"};function a(r,e,l,d,g,h){return i(),o("div",{"data-pagefind-body":!0},e[0]||(e[0]=[n('<h1 id="english-for-post-writing-guidelines" tabindex="-1">English for post Writing Guidelines <a class="header-anchor" href="#english-for-post-writing-guidelines" aria-label="Permalink to &quot;English for post Writing Guidelines&quot;">​</a></h1><p>When contributing to this repository, please follow the following guidelines for writing posts:</p><h2 id="_1-tags-and-categories" tabindex="-1">1. Tags and Categories <a class="header-anchor" href="#_1-tags-and-categories" aria-label="Permalink to &quot;1. Tags and Categories&quot;">​</a></h2><ul><li><strong>tag</strong>: Each post should include relevant tags that describe the content and make it easier for readers to find. Tags should be separated by commas and should be chosen from the pre-defined list of tags (if applicable).</li></ul><h2 id="_2-formatting" tabindex="-1">2. Formatting <a class="header-anchor" href="#_2-formatting" aria-label="Permalink to &quot;2. Formatting&quot;">​</a></h2><ul><li><p><strong>Title</strong>: The title of the post should be clear and concise, and should accurately reflect the content of the post.</p></li><li><p><strong>Header Structure</strong>: Use headers (#, ##, ###, etc.) to organize the content of the post and make it easier for readers to navigate.</p></li><li><p><strong>Images and Media</strong>: If applicable, include relevant images and media to enhance the post. Please ensure that all media is properly credited and licensed for use.</p></li><li><p><strong>Links</strong>: Use links to provide additional information or sources for the content in the post.</p></li></ul><h2 id="_3-content" tabindex="-1">3. Content <a class="header-anchor" href="#_3-content" aria-label="Permalink to &quot;3. Content&quot;">​</a></h2><ul><li><p><strong>Originality</strong>: The content of the post should be original and not copied from other sources.</p></li><li><p><strong>Quality</strong>: The content should be well-written, informative, and engaging. Avoid using jargon or technical language that may be difficult for readers to understand.</p></li><li><p><strong>Length</strong>: While there is no strict length requirement, posts should be long enough to provide a comprehensive and meaningful discussion of the topic.</p></li></ul><h2 id="_4-submission" tabindex="-1">4. Submission <a class="header-anchor" href="#_4-submission" aria-label="Permalink to &quot;4. Submission&quot;">​</a></h2><ul><li><p><strong>File Format</strong>: Posts should be submitted in Markdown format (.md).</p></li><li><p><strong>Naming Convention</strong>: Posts should be named using the following convention: <code>notes/posts/XXX.md</code>.</p></li><li><p><strong>Pull Requests</strong>: Submit your post as a pull request to the appropriate branch of the repository. Please include a brief description of the post and any relevant tags or categories in the pull request.</p></li></ul><p>By following these guidelines, you can help ensure that the posts in this repository are high-quality, informative, and easy to find. Thank you for your contribution!</p>',11)]))}const c=t(s,[["render",a]]);export{u as __pageData,c as default};
