mapper.select:
select count(DISTINCT j.id)
from t_job j


        and j.job_name like concat('%',#{jobSearchVO.jobName},'%')


        and j.job_group = #{jobSearchVO.jobGroup}


        and j.status = #{jobSearchVO.status}

select *
from t_job j


        and j.job_name like concat('%',#{jobSearchVO.jobName},'%')


        and j.job_group = #{jobSearchVO.jobGroup}


        and j.status = #{jobSearchVO.status}


ORDER BY j.status DESC
LIMIT #{current},#{size}
